document.getElementById("monthly-price").textContent=0,document.getElementById("vat").textContent=0,document.getElementById("fees").textContent=0,document.getElementById("total-price").textContent=0;let selectedDuration=3;async function populateServers(){const e=document.getElementById("category").value,t=document.querySelector("#server-table tbody");t.innerHTML="";for(const n of serverData[e]){const e=document.createElement("tr");e.innerHTML=`\n                <td><button class="select-button" onclick="selectServer('${n.id}', ${n.monthly}, ${n.hourly});">Ausw√§hlen</button></td>    \n                <td>${n.vCPU}</td>\n                <td>${n.RAM}</td>\n                <td>${n.SSD}</td>\n                <td>${n.monthly.toFixed(2)}</td>\n            `,t.appendChild(e)}}function calculatePrice(e){const t=(.19*e).toFixed(2),n=(parseFloat(e)+parseFloat(t)+parseFloat(.05)).toFixed(2);document.getElementById("monthly-price").textContent=e,document.getElementById("vat").textContent=t,document.getElementById("fees").textContent=.05,document.getElementById("total-price").textContent=n,document.getElementById("selected-duration").textContent=`${selectedDuration} Tage`}function selectServer(e,t,n){if(console.log(`Selected Server: ${e}`),"3 Tage"==document.getElementById("selected-duration").textContent){calculatePrice((24*n*3).toFixed(2))}else if("30 Tage"==document.getElementById("selected-duration").textContent){calculatePrice(t.toFixed(2))}else if("90 Tage"==document.getElementById("selected-duration").textContent){calculatePrice((3*t).toFixed(2))}else if("180 Tage"==document.getElementById("selected-duration").textContent){calculatePrice((6*t).toFixed(2))}else if("365 Tage"==document.getElementById("selected-duration").textContent){calculatePrice((12*t).toFixed(2))}else alert("Es ist ein Fehler in der Funktion selectServer() aufgetreten")}function setDuration(e){selectedDuration=e,calculatePrice(document.querySelector("#server-table tbody tr button").textContent)}populate